"""
FastAPI application - converted from Express.js backend
"""
import os
from typing import List, Optional
from fastapi import FastAPI, Depends, HTTPException, status, Query
from fastapi.middleware.cors import CORSMiddleware
from fastapi.staticfiles import StaticFiles
from fastapi.responses import FileResponse
from sqlalchemy.orm import Session, joinedload
from sqlalchemy import func, case, and_, desc, Boolean
from datetime import timedelta
import random

from .database import get_db, create_tables
from .models import User, Problem, Submission, CommunityPost, PostLike, PostComment
from .schemas import (
    UserCreate, UserResponse, UserLogin, LoginResponse, RegisterResponse,
    ProblemResponse, SubmissionCreate, SubmissionResponse,
    CommunityPostCreate, CommunityPostResponse,
    PostCommentCreate, PostCommentResponse
)
from .auth import (
    get_password_hash, verify_password, create_access_token,
    get_current_user, get_current_user_optional
)

# Create FastAPI app
app = FastAPI(
    title="SQLGym API",
    description="A gamified SQL learning platform API",
    version="1.0.0"
)

# Add CORS middleware
app.add_middleware(
    CORSMiddleware,
    allow_origins=["*"],  # In production, replace with actual frontend URL
    allow_credentials=True,
    allow_methods=["*"],
    allow_headers=["*"],
)

# Create tables on startup
@app.on_event("startup")
def startup_event():
    from .database import run_schema_migrations
    run_schema_migrations()  # Run migrations first
    create_tables()  # Then create any missing tables

# Development/fallback root endpoint
# Development/fallback root endpoint  
@app.get("/")
def read_root():
    if os.path.exists("dist/public/index.html"):
        return FileResponse("dist/public/index.html")
    return {"message": "SQLGym FastAPI Backend - Please run 'npm run build' first"}

# Mount static assets for production
if os.path.exists("dist/public/assets"):
    app.mount("/assets", StaticFiles(directory="dist/public/assets"), name="assets")

# Authentication endpoints
@app.post("/api/auth/register", response_model=RegisterResponse, response_model_by_alias=True)
def register(user_data: UserCreate, db: Session = Depends(get_db)):
    # Check if user already exists
    existing_user = db.query(User).filter(User.email == user_data.email).first()
    if existing_user:
        raise HTTPException(
            status_code=status.HTTP_400_BAD_REQUEST,
            detail="User already exists"
        )

    # Check if username is taken
    existing_username = db.query(User).filter(User.username == user_data.username).first()
    if existing_username:
        raise HTTPException(
            status_code=status.HTTP_400_BAD_REQUEST,
            detail="Username already taken"
        )

    # Hash password if provided
    password_hash = None
    if user_data.password is not None:
        password_hash = get_password_hash(user_data.password)

    # Create user
    user = User(
        username=user_data.username,
        email=user_data.email,
        password_hash=password_hash,
        first_name=user_data.first_name,
        last_name=user_data.last_name,
        profile_image_url=user_data.profile_image_url,
        google_id=user_data.google_id,
        github_id=user_data.github_id,
        auth_provider=user_data.auth_provider
    )

    db.add(user)
    db.commit()
    db.refresh(user)

    # Generate JWT token
    access_token = create_access_token(
        data={"userId": user.id, "username": user.username}
    )

    return RegisterResponse(
        token=access_token,
        user=UserResponse.from_orm(user)
    )

@app.post("/api/auth/login", response_model=LoginResponse, response_model_by_alias=True)
def login(login_data: UserLogin, db: Session = Depends(get_db)):
    # Find user by email
    user = db.query(User).filter(User.email == login_data.email).first()
    if not user:
        raise HTTPException(
            status_code=status.HTTP_401_UNAUTHORIZED,
            detail="Invalid credentials"
        )

    # Verify password
    if not user.password_hash or not verify_password(login_data.password, user.password_hash):
        raise HTTPException(
            status_code=status.HTTP_401_UNAUTHORIZED,
            detail="Invalid credentials"
        )

    # Generate JWT token
    access_token = create_access_token(
        data={"userId": user.id, "username": user.username}
    )

    return LoginResponse(
        token=access_token,
        user=UserResponse.from_orm(user)
    )

@app.get("/api/auth/user", response_model=UserResponse, response_model_by_alias=True)
def get_current_user_info(current_user: User = Depends(get_current_user)):
    return UserResponse.from_orm(current_user)

# Problem endpoints
@app.get("/api/problems", response_model=List[ProblemResponse], response_model_by_alias=True)
def get_problems(
    difficulty: Optional[str] = Query(None),
    current_user: Optional[User] = Depends(get_current_user_optional),
    db: Session = Depends(get_db)
):
    # Base query with solved count
    query = db.query(
        Problem,
        func.coalesce(
            func.count(
                case(
                    (Submission.is_correct == True, Submission.user_id),
                    else_=None
                ).distinct()
            ), 0
        ).label("solved_count")
    ).outerjoin(Submission)

    # Add user-specific solved status if authenticated
    if current_user:
        query = query.add_columns(
            func.coalesce(
                func.max(
                    case(
                        (and_(Submission.user_id == current_user.id, Submission.is_correct == True), 1),
                        else_=0
                    )
                ), 0
            ).label("is_user_solved")
        )
    else:
        query = query.add_columns(func.cast(False, Boolean).label("is_user_solved"))

    # Apply difficulty filter
    if difficulty:
        query = query.filter(Problem.difficulty == difficulty)

    # Group by problem and order by title
    results = query.group_by(Problem.id).order_by(Problem.title).all()

    # Format response
    problems = []
    for problem, solved_count, is_user_solved in results:
        problem_data = ProblemResponse.from_orm(problem)
        problem_data.solved_count = int(solved_count)
        problem_data.is_user_solved = bool(is_user_solved) if current_user else False
        problems.append(problem_data)

    return problems

@app.get("/api/problems/{problem_id}", response_model=ProblemResponse, response_model_by_alias=True)
def get_problem(problem_id: str, db: Session = Depends(get_db)):
    problem = db.query(Problem).filter(Problem.id == problem_id).first()
    if not problem:
        raise HTTPException(
            status_code=status.HTTP_404_NOT_FOUND,
            detail="Problem not found"
        )
    return ProblemResponse.from_orm(problem)

# Submission endpoints
@app.post("/api/submissions", response_model=SubmissionResponse, response_model_by_alias=True)
def create_submission(
    submission_data: SubmissionCreate,
    current_user: User = Depends(get_current_user),
    db: Session = Depends(get_db)
):
    # Simulate SQL query execution (simplified version)
    problem = db.query(Problem).filter(Problem.id == submission_data.problem_id).first()
    if not problem:
        raise HTTPException(
            status_code=status.HTTP_404_NOT_FOUND,
            detail="Problem not found"
        )

    # Simple validation logic (same as Express.js version)
    is_correct = simulate_query_execution(submission_data.query, problem)
    execution_time = random.randint(50, 550)  # Random execution time

    # Create submission
    submission = Submission(
        user_id=current_user.id,
        problem_id=submission_data.problem_id,
        query=submission_data.query,
        is_correct=is_correct,
        execution_time=execution_time
    )

    db.add(submission)

    # If correct, update user progress
    if is_correct:
        current_user.problems_solved += 1
        db.add(current_user)

    db.commit()
    db.refresh(submission)

    return SubmissionResponse.from_orm(submission)

@app.get("/api/submissions/user/{user_id}", response_model=List[SubmissionResponse], response_model_by_alias=True)
def get_user_submissions(
    user_id: str,
    current_user: User = Depends(get_current_user),
    db: Session = Depends(get_db)
):
    # Users can only view their own submissions
    if user_id != current_user.id:
        raise HTTPException(
            status_code=status.HTTP_403_FORBIDDEN,
            detail="Access denied"
        )

    submissions = db.query(Submission).filter(
        Submission.user_id == user_id
    ).order_by(desc(Submission.submitted_at)).all()

    return [SubmissionResponse.from_orm(sub) for sub in submissions]

# Leaderboard endpoint
@app.get("/api/leaderboard", response_model=List[UserResponse], response_model_by_alias=True)
def get_leaderboard(
    limit: Optional[int] = Query(50),
    db: Session = Depends(get_db)
):
    users = db.query(User).order_by(
        desc(User.problems_solved)
    ).limit(limit).all()

    return [UserResponse.from_orm(user) for user in users]

# Community endpoints
@app.get("/api/community/posts", response_model=List[CommunityPostResponse], response_model_by_alias=True)
def get_community_posts(db: Session = Depends(get_db)):
    posts = db.query(CommunityPost).options(
        joinedload(CommunityPost.user)
    ).order_by(desc(CommunityPost.created_at)).all()

    return [CommunityPostResponse.from_orm(post) for post in posts]

@app.post("/api/community/posts", response_model=CommunityPostResponse, response_model_by_alias=True)
def create_community_post(
    post_data: CommunityPostCreate,
    current_user: User = Depends(get_current_user),
    db: Session = Depends(get_db)
):
    post = CommunityPost(
        user_id=current_user.id,
        content=post_data.content,
        code_snippet=post_data.code_snippet
    )

    db.add(post)
    db.commit()
    db.refresh(post)

    # Load user relationship
    post = db.query(CommunityPost).options(
        joinedload(CommunityPost.user)
    ).filter(CommunityPost.id == post.id).first()

    return CommunityPostResponse.from_orm(post)

@app.post("/api/community/posts/{post_id}/like")
def like_post(
    post_id: str,
    current_user: User = Depends(get_current_user),
    db: Session = Depends(get_db)
):
    # Check if already liked
    existing_like = db.query(PostLike).filter(
        and_(PostLike.user_id == current_user.id, PostLike.post_id == post_id)
    ).first()

    if existing_like:
        raise HTTPException(
            status_code=status.HTTP_400_BAD_REQUEST,
            detail="Post already liked"
        )

    # Create like
    like = PostLike(user_id=current_user.id, post_id=post_id)
    db.add(like)

    # Update post likes count
    post = db.query(CommunityPost).filter(CommunityPost.id == post_id).first()
    if post:
        post.likes += 1
        db.add(post)

    db.commit()
    return {"message": "Post liked successfully"}

@app.delete("/api/community/posts/{post_id}/like")
def unlike_post(
    post_id: str,
    current_user: User = Depends(get_current_user),
    db: Session = Depends(get_db)
):
    # Find and delete like
    like = db.query(PostLike).filter(
        and_(PostLike.user_id == current_user.id, PostLike.post_id == post_id)
    ).first()

    if not like:
        raise HTTPException(
            status_code=status.HTTP_404_NOT_FOUND,
            detail="Like not found"
        )

    db.delete(like)

    # Update post likes count
    post = db.query(CommunityPost).filter(CommunityPost.id == post_id).first()
    if post:
        post.likes = max(0, post.likes - 1)
        db.add(post)

    db.commit()
    return {"message": "Post unliked successfully"}

@app.get("/api/community/posts/{post_id}/comments", response_model=List[PostCommentResponse], response_model_by_alias=True)
def get_post_comments(post_id: str, db: Session = Depends(get_db)):
    comments = db.query(PostComment).options(
        joinedload(PostComment.user)
    ).filter(PostComment.post_id == post_id).order_by(PostComment.created_at).all()

    return [PostCommentResponse.from_orm(comment) for comment in comments]

@app.post("/api/community/posts/{post_id}/comments", response_model=PostCommentResponse, response_model_by_alias=True)
def create_post_comment(
    post_id: str,
    comment_data: PostCommentCreate,
    current_user: User = Depends(get_current_user),
    db: Session = Depends(get_db)
):
    comment = PostComment(
        user_id=current_user.id,
        post_id=post_id,
        content=comment_data.content
    )

    db.add(comment)

    # Update post comments count
    post = db.query(CommunityPost).filter(CommunityPost.id == post_id).first()
    if post:
        post.comments += 1
        db.add(post)

    db.commit()
    db.refresh(comment)

    # Load user relationship
    comment = db.query(PostComment).options(
        joinedload(PostComment.user)
    ).filter(PostComment.id == comment.id).first()

    return PostCommentResponse.from_orm(comment)

# Helper function for query simulation
def simulate_query_execution(query: str, problem: Problem) -> bool:
    """
    Simplified query validation (same logic as Express.js version)
    """
    normalized_query = query.lower().strip()

    # Basic checks for different problem types
    if "sum" in problem.title.lower():
        return (
            "select" in normalized_query and
            "from" in normalized_query and
            ("sum" in normalized_query or "+" in normalized_query)
        )

    if "join" in problem.title.lower():
        return (
            "select" in normalized_query and
            "from" in normalized_query and
            "join" in normalized_query
        )

    # Default validation: must contain SELECT and FROM
    return "select" in normalized_query and "from" in normalized_query

# SPA fallback route - handle all non-API routes (must be last)
# SPA fallback route - handle all non-API routes (must be last)
@app.get("/{full_path:path}")
def spa_fallback(full_path: str):
    # Don't handle API routes
    if full_path.startswith("api/"):
        raise HTTPException(status_code=404, detail="API endpoint not found")

    # Don't handle asset files
    if full_path.startswith("assets/") or "." in full_path.split("/")[-1]:
        raise HTTPException(status_code=404, detail="File not found")

    # Serve SPA for all other routes
    index_path = "dist/public/index.html"
    if os.path.exists(index_path):
        return FileResponse(index_path)

    # Fallback if no built frontend
    raise HTTPException(status_code=404, detail="Frontend not built")
if __name__ == "__main__":
    import uvicorn
    port = int(os.getenv("PORT", 8000))
    host = "0.0.0.0" if os.getenv("REPL_ID") else "127.0.0.1"
    uvicorn.run(app, host=host, port=port)

