import { useState, useRef, useEffect, useCallback } from 'react';

export interface UseTimerReturn {
  seconds: number;
  isRunning: boolean;
  formattedTime: string;
  start: () => void;
  pause: () => void;
  reset: () => void;
  toggle: () => void;
}

export function useTimer(): UseTimerReturn {
  const [seconds, setSeconds] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const timerRef = useRef<number | null>(null);

  // Timer effect - optimized to only run when isRunning changes
  useEffect(() => {
    if (isRunning) {
      timerRef.current = window.setInterval(() => {
        setSeconds((prev) => prev + 1);
      }, 1000);
    } else {
      if (timerRef.current) {
        window.clearInterval(timerRef.current);
        timerRef.current = null;
      }
    }

    return () => {
      if (timerRef.current) {
        window.clearInterval(timerRef.current);
      }
    };
  }, [isRunning]);

  // Memoized format function
  const formattedTime = useCallback(() => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes.toString().padStart(2, "0")}:${remainingSeconds
      .toString()
      .padStart(2, "0")}`;
  }, [seconds])();

  // Timer controls - memoized to prevent recreation
  const start = useCallback(() => {
    setIsRunning(true);
  }, []);

  const pause = useCallback(() => {
    setIsRunning(false);
  }, []);

  const reset = useCallback(() => {
    setIsRunning(false);
    setSeconds(0);
  }, []);

  const toggle = useCallback(() => {
    setIsRunning((prev) => !prev);
  }, []);

  return {
    seconds,
    isRunning,
    formattedTime,
    start,
    pause,
    reset,
    toggle,
  };
}

client/src/components/OutputPanel.tsx.content:
import React, { useMemo } from 'react';

interface OutputPanelProps {
  result: {
    success: boolean;
    results?: any[];
    execution_time_ms?: number;
    rows_affected?: number;
    console_info?: string;
    error?: string;
    feedback?: string[];
    test_results?: any[];
  } | null;
  isLoading: boolean;
}

const OptimizedTable = ({ data }: { data: any[] }) => {
  const { headers, rows } = useMemo(() => {
    if (!data || data.length === 0) return { headers: [], rows: [] };

    const headers = Object.keys(data[0]);
    const rows = data.map(row => headers.map(header => row[header]));

    return { headers, rows };
  }, [data]);

  if (headers.length === 0) {
    return <div className="text-gray-500 italic p-4">No data to display</div>;
  }

  return (
    <div className="bg-white border border-gray-200 rounded-lg overflow-hidden max-h-full">
      <div className="overflow-auto max-h-96">
        <table className="w-full">
          <thead className="sticky top-0 bg-gray-50">
            <tr className="border-b border-gray-200">
              {headers.map((header, i) => (
                <th 
                  key={i} 
                  className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                >
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {rows.map((row, i) => (
              <tr key={i} className="hover:bg-gray-50">
                {row.map((cell, j) => (
                  <td key={j} className="px-4 py-3 text-sm text-gray-900">
                    {String(cell ?? '')}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default function OutputPanel({ result, isLoading }: OutputPanelProps) {
  if (isLoading) {
    return (
      <div className="h-full bg-gray-50 flex items-center justify-center">
        <div className="text-gray-600">Executing query...</div>
      </div>
    );
  }

  if (!result) {
    return (
      <div className="h-full bg-gray-50 flex items-center justify-center">
        <div className="text-gray-500">Ready to execute queries...</div>
      </div>
    );
  }

  const executionTimeSeconds = result.execution_time_ms ? (result.execution_time_ms / 1000).toFixed(5) : '0.00000';

  // Add this handler for the "View in new tab" functionality
  const handleViewInNewTab = () => {
    if (!result?.results || result.results.length === 0) return;

    const htmlContent = `
      <!DOCTYPE html>
      <html>
      <head>
        <title>Query Results</title>
        <style>
          body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif; margin: 20px; }
          table { width: 100%; border-collapse: collapse; border: 1px solid #e5e5e5; }
          th { background: #f9f9f9; padding: 12px; text-align: left; border-bottom: 1px solid #e5e5e5; font-weight: 600; }
          td { padding: 12px; border-bottom: 1px solid #f0f0f0; }
          tr:hover { background: #f9f9f9; }
        </style>
      </head>
      <body>
        <h2>Query Results</h2>
        <p>Execution time: ${executionTimeSeconds} seconds | ${result.results.length} rows</p>
        <table>
          <thead>
            <tr>
              ${Object.keys(result.results[0]).map(header => `<th>${header}</th>`).join('')}
            </tr>
          </thead>
          <tbody>
            ${result.results.map(row => 
              `<tr>${Object.values(row).map(cell => `<td>${String(cell ?? '')}</td>`).join('')}</tr>`
            ).join('')}
          </tbody>
        </table>
      </body>
      </html>
    `;

    const blob = new Blob([htmlContent], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    window.open(url, '_blank');

    // Clean up the URL object after a delay
    setTimeout(() => URL.revokeObjectURL(url), 1000);
  };

  return (
    <div className="h-full bg-gray-50 flex flex-col">
      {/* Header with execution info */}
      <div className="flex-shrink-0 px-4 py-3 bg-white border-b border-gray-200 flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <span className="text-sm text-gray-700">
            Execution time: {executionTimeSeconds} seconds
          </span>
          {result.rows_affected !== undefined && (
            <span className="text-sm text-gray-500">
              {result.rows_affected} rows
            </span>
          )}
        </div>
        <button 
          onClick={handleViewInNewTab}
          className="px-3 py-1 text-sm text-blue-600 border border-blue-300 rounded hover:bg-blue-50"
          disabled={!result?.results || result.results.length === 0}
          data-testid="button-view-new-tab"
        >
          View the output in a new tab
        </button>
      </div>

      {/* Results area */}
      <div className="flex-1 p-4 overflow-auto">
        {result.success && result.results && result.results.length > 0 ? (
          <OptimizedTable data={result.results} />
        ) : !result.success ? (
          <div className="bg-red-50 border border-red-200 rounded-lg p-4">
            <div className="text-red-800 font-medium">Query Error</div>
            <div className="text-red-700 mt-1 whitespace-pre-wrap">
              {result.error || 'Query failed'}
            </div>
          </div>
        ) : (
          <div className="text-center text-gray-500 py-8">
            Query executed successfully - no rows returned
          </div>
        )}
      </div>

      {/* Test results section */}
      {result.test_results && result.test_results.length > 0 && (
        <div className="flex-shrink-0 border-t border-gray-200 p-4 bg-white">
          <h3 className="text-gray-900 font-medium mb-2">Test Results:</h3>
          <div className="space-y-1">
            {result.test_results.map((test, index) => (
              <div key={index} className={`text-sm flex items-center space-x-2`}>
                <span className={test.passed ? 'text-green-600' : 'text-red-600'}>
                  {test.passed ? 'âœ“' : 'âœ—'}
                </span>
                <span className="text-gray-700">{test.name}</span>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/ProblemTabsContent.content:
import { memo, useState } from 'react';
import { Code2, MessageSquare, CheckCircle, BookOpen, Heart, Reply, Send, ChevronUp, ChevronDown, Lock } from 'lucide-react';
import { useQuery, useMutation } from "@tanstack/react-query";
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Textarea } from '@/components/ui/textarea';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { useToast } from '@/hooks/use-toast';
import { useAuth } from '@/hooks/use-auth';
import { apiRequest, queryClient } from '@/lib/queryClient';
import ProblemDescriptionTab from '@/components/ProblemDescriptionTab';
import AnswersScreen from '@/components/AnswersScreen';
import ResultComparisonTable from './ResultComparisonTable';
import { RichTextEditor } from '@/components/RichTextEditor';
import { MarkdownRenderer } from '@/components/MarkdownRenderer';

interface Problem {
  id?: string;
  question?: {
    description?: string;
    tables?: any[];
    expectedOutput?: any[];
  };
  hints?: string[];
  tags?: string[];
  premium?: boolean | null;
}

interface Solution {
  id: string;
  problem_id: string;
  title: string;
  explanation: string;
  code: string;
  approach: string;
  time_complexity: string;
  space_complexity: string;
  tags: string[];
  created_at: string;
}

interface Submission {
  id: string;
  isCorrect: boolean;
  submittedAt: string;
  executionTime?: number;
}

interface TestResult {
  test_case_id: string;
  test_case_name: string;
  is_hidden: boolean;
  is_correct: boolean;
  score: number;
  feedback: string[];
  execution_time_ms: number;
  execution_status: string;
  validation_details: any;
  user_output: any[];
  expected_output: any[];
  output_matches: boolean;
}

interface SubmissionResult {
  success: boolean;
  is_correct: boolean;
  score: number;
  feedback: string[];
  test_results: TestResult[];
  submission_id: string;
  execution_stats: {
    avg_time_ms: number;
    max_time_ms: number;
    memory_used_mb: number;
  };
}

interface ProblemTabsContentProps {
  problem?: Problem;
  userSubmissions?: Submission[];
  latestSubmissionResult?: SubmissionResult | null;
  className?: string;
  activeTab?: string;
  onTabChange?: (value: string) => void;
  problemId?: string;
}

interface User {
  id: string;
  username: string;
  profileImageUrl?: string;
}

interface Discussion {
  id: string;
  userId: string;
  problemId: string;
  content: string;
  codeSnippet?: string;
  likes: number;
  comments: number;
  createdAt: string;
  user: User;
  isLiked?: boolean;
}

interface Comment {
  id: string;
  postId: string;
  userId: string;
  parentId?: string;
  content: string;
  createdAt: string;
  user: User;
  replies?: Comment[];
}

const NestedComment = memo(function NestedComment({
  comment,
  discussionId,
  onReply,
  depth = 0
}: {
  comment: Comment;
  discussionId: string;
  onReply: (content: string, parentId?: string) => void;
  depth: number;
}) {
  const [showReplyBox, setShowReplyBox] = useState(false);
  const [replyContent, setReplyContent] = useState('');
  const { toast } = useToast();

  const replyMutation = useMutation({
    mutationFn: async (data: { content: string; parentId?: string }) => {
      return apiRequest('POST', `/api/community/posts/${discussionId}/comments`, { 
          content: data.content,
          parent_id: data.parentId 
        });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/community/posts/${discussionId}/comments`] });
      setReplyContent('');
      setShowReplyBox(false);
    },
    onError: (error) => {
      console.error("Failed to post reply:", error);
    },
  });

  const handleSubmitReply = () => {
    if (!replyContent.trim()) return;
    replyMutation.mutate({ content: replyContent, parentId: comment.id });
  };

  const maxDepth = 3; // Limit nesting depth to avoid infinite nesting
  const isMaxDepth = depth >= maxDepth;

  return (
    <div className={`${depth > 0 ? 'ml-6 mt-3' : ''}`} data-testid={`comment-${comment.id}`}>
      <div className="flex items-start space-x-3">
        <Avatar className={`${depth > 0 ? 'w-6 h-6' : 'w-8 h-8'}`}>
          <AvatarImage src={comment.user.profileImageUrl} alt={comment.user.username} />
          <AvatarFallback>
            {comment.user.username?.charAt(0).toUpperCase() || 'U'}
          </AvatarFallback>
        </Avatar>

        <div className="flex-1">
          <div className="flex items-center space-x-2 mb-1">
            <span className={`font-medium text-foreground ${depth > 0 ? 'text-sm' : 'text-sm'}`}>
              {comment.user.username}
            </span>
            <span className="text-xs text-muted-foreground">
              {new Date(comment.createdAt).toLocaleDateString()}
            </span>
          </div>

          <p className={`text-foreground whitespace-pre-wrap ${depth > 0 ? 'text-sm' : 'text-sm'}`}>
            {comment.content}
          </p>

          {/* Reply button */}
          {!isMaxDepth && (
            <div className="mt-2">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setShowReplyBox(!showReplyBox)}
                className="text-xs text-muted-foreground hover:text-blue-500 h-6 px-2"
                data-testid={`button-reply-${comment.id}`}
              >
                <Reply className="w-3 h-3 mr-1" />
                Reply
              </Button>
            </div>
          )}

          {/* Reply input box */}
          {showReplyBox && (
            <div className="mt-3 space-y-2">
              <Textarea
                placeholder={`Reply to ${comment.user.username}...`}
                value={replyContent}
                onChange={(e) => setReplyContent(e.target.value)}
                rows={2}
                className="resize-none text-sm"
                data-testid={`textarea-nested-reply-${comment.id}`}
              />
              <div className="flex justify-end space-x-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => {
                    setShowReplyBox(false);
                    setReplyContent('');
                  }}
                  className="h-8 text-xs"
                  data-testid={`button-cancel-reply-${comment.id}`}
                >
                  Cancel
                </Button>
                <Button
                  size="sm"
                  onClick={handleSubmitReply}
                  disabled={!replyContent.trim() || replyMutation.isPending}
                  className="h-8 text-xs"
                  data-testid={`button-submit-reply-${comment.id}`}
                >
                  {replyMutation.isPending ? "Posting..." : "Reply"}
                </Button>
              </div>
            </div>
          )}

          {/* Nested replies */}
          {comment.replies && comment.replies.length > 0 && (
            <div className="mt-3 space-y-3">
              {comment.replies.map((reply) => (
                <NestedComment
                  key={reply.id}
                  comment={reply}
                  discussionId={discussionId}
                  onReply={onReply}
                  depth={depth + 1}
                />
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
});

const OutputTable = ({ data, title }: { data: any[]; title: string }) => {
  if (!data || data.length === 0) {
    return (
      <div className="bg-gray-50 border border-gray-200 rounded-lg p-4">
        <h3 className="text-sm font-medium text-gray-700 mb-2">{title}</h3>
        <div className="text-gray-500 italic text-sm">No data to display</div>
      </div>
    );
  }

  const headers = Object.keys(data[0]);

  return (
    <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
      <div className="bg-gray-50 px-4 py-2 border-b border-gray-200">
        <h3 className="text-sm font-medium text-gray-700">{title}</h3>
      </div>
      <div className="overflow-auto max-h-80 min-h-32">
        <table className="w-full">
          <thead className="sticky top-0 bg-gray-50 z-10">
            <tr className="border-b border-gray-200">
              {headers.map((header, i) => (
                <th 
                  key={i} 
                  className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                >
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {data.map((row, i) => (
              <tr key={i} className="hover:bg-gray-50">
                {headers.map((header, j) => (
                  <td key={j} className="px-4 py-2 text-sm text-gray-900">
                    {String(row[header] ?? '')}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

const DiscussionCard = memo(function DiscussionCard({ 
  discussion, 
  onLike, 
  onComment 
}: { 
  discussion: Discussion;
  onLike: (id: string, isLiked: boolean) => void;
  onComment: (id: string) => void;
}) {
  const [showComments, setShowComments] = useState(discussion.comments > 0);
  const [replyContent, setReplyContent] = useState('');
  const { toast } = useToast();

  const { data: comments = [] } = useQuery({
    queryKey: [`/api/community/posts/${discussion.id}/comments`, discussion.id],
    enabled: showComments,
  });

  const replyMutation = useMutation({
    mutationFn: async (content: string) => {
      return apiRequest('POST', `/api/community/posts/${discussion.id}/comments`, { content });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/community/posts/${discussion.id}/comments`] });
      queryClient.invalidateQueries({ queryKey: [`/api/problems/${discussion.problemId}/discussions`] });
      setReplyContent('');
    },
    onError: (error) => {
      console.error("Failed to post reply:", error);
    },
  });

  const handleReply = () => {
    if (!replyContent.trim()) return;
    replyMutation.mutate(replyContent);
  };

  return (
    <Card className="mb-4" data-testid={`discussion-${discussion.id}`}>
      <CardContent className="p-6">
        <div className="flex items-start space-x-4">
          <Avatar className="w-10 h-10">
            <AvatarImage src={discussion.user.profileImageUrl} alt={discussion.user.username} />
            <AvatarFallback>
              {discussion.user.username?.charAt(0).toUpperCase() || 'U'}
            </AvatarFallback>
          </Avatar>

          <div className="flex-1">
            <div className="flex items-center space-x-2 mb-2">
              <span className="font-semibold text-foreground" data-testid={`text-username-${discussion.id}`}>
                {discussion.user.username}
              </span>
              <span className="text-sm text-muted-foreground">
                {new Date(discussion.createdAt).toLocaleDateString()}
              </span>
            </div>

            <div className="mb-3" data-testid={`text-content-${discussion.id}`}>
              <MarkdownRenderer content={discussion.content} />
            </div>

            {discussion.codeSnippet && (
              <div className="bg-muted/50 rounded-lg p-3 mb-3">
                <pre className="text-sm font-mono whitespace-pre-wrap" data-testid={`code-snippet-${discussion.id}`}>
                  {discussion.codeSnippet}
                </pre>
              </div>
            )}

            <div className="flex items-center space-x-4">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onLike(discussion.id, !!discussion.isLiked)}
                className="text-muted-foreground hover:text-red-500"
                data-testid={`button-like-${discussion.id}`}
              >
                <Heart className={`w-4 h-4 mr-1 ${discussion.isLiked ? 'fill-current text-red-500' : ''}`} />
                {discussion.likes}
              </Button>

              <Button
                variant="ghost"
                size="sm"
                onClick={() => setShowComments(!showComments)}
                className="text-muted-foreground hover:text-blue-500"
                data-testid={`button-comments-${discussion.id}`}
              >
                <MessageSquare className="w-4 h-4 mr-1" />
