}

interface HelpfulLink {
  id: string;
  userId: string;
  title: string;
  url: string;
  createdAt: string;
  user: {
    id: string;
    username: string;
    firstName?: string;
    lastName?: string;
  };
}

function HelpfulLinksSection() {
  const { data: links, isLoading } = useQuery<HelpfulLink[]>({
    queryKey: ['/api/helpful-links'],
  });

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center space-x-2">
          <Link2 className="w-5 h-5 text-primary" />
          <span>Helpful Resources</span>
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        {isLoading ? (
          <div className="space-y-3">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="h-16 bg-muted rounded-lg animate-pulse" />
            ))}
          </div>
        ) : links && links.length > 0 ? (
          <div className="space-y-3">
            {links.map((link) => (
              <div
                key={link.id}
                className="p-3 bg-muted/50 rounded-lg hover:bg-muted transition-colors"
                data-testid={`link-item-${link.id}`}
              >
                <a
                  href={link.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="font-medium text-sm text-foreground hover:text-primary flex items-center space-x-1"
                  data-testid={`link-url-${link.id}`}
                >
                  <span className="truncate">{link.title}</span>
                  <ExternalLink className="w-3 h-3 flex-shrink-0" />
                </a>
                <p className="text-xs text-muted-foreground mt-1">
                  by {link.user.firstName && link.user.lastName 
                    ? `${link.user.firstName} ${link.user.lastName}` 
                    : link.user.username}
                </p>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-8 text-muted-foreground">
            <Link2 className="w-12 h-12 mx-auto mb-3 opacity-20" />
            <p className="text-sm">No helpful links yet</p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

export default function Home() {
  const { user } = useAuth();

  const { data: problems, isLoading: problemsLoading } = useQuery({
    queryKey: ['/api/problems'],
    queryFn: () => problemsApi.getAll(),
  });

  const recentProblems = problems?.slice(0, 3) || [];

  const bannerContent = useMemo(() => {
    const { message, emoji } = getDynamicMessage(user?.problemsSolved || 0);
    return { message, emoji };
  }, [user?.problemsSolved]);

  const displayName = useMemo(() => {
    if (user?.firstName && user?.lastName) {
      return `${user.firstName} ${user.lastName}`;
    }
    return user?.username || 'there';
  }, [user?.firstName, user?.lastName, user?.username]);

  return (
    <div className="min-h-screen bg-white" style={{ backgroundColor: '#FFFFFF' }}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-foreground mb-2">
            Welcome back, <span className="text-primary">{displayName}</span>! {bannerContent.emoji}
          </h1>
          <p className="text-xl text-muted-foreground">
            {bannerContent.message}
          </p>
          {user?.companyName && (
            <div className="flex items-center mt-2 text-muted-foreground">
              <Building className="h-4 w-4 mr-2" />
              <span className="text-sm">{user.companyName}</span>
            </div>
          )}
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-2 space-y-8">
            {/* Progress Overview */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <TrendingUp className="w-5 h-5 text-primary" />
                  <span>Your Progress</span>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-6">
                <div className="text-center">
                  <div className="text-3xl font-bold text-green-600">{user?.problemsSolved || 0}</div>
                  <div className="text-sm text-muted-foreground">Problems Solved</div>
                  <p className="text-sm text-muted-foreground mt-2">Keep solving problems to improve your skills!</p>
                </div>
              </CardContent>
            </Card>

            {/* Quick Actions */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <Target className="w-5 h-5 text-primary" />
                  <span>Quick Actions</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid md:grid-cols-2 gap-4">
                  <Link href="/problems">
                    <Button 
                      className="w-full dumbbell-btn bg-primary text-primary-foreground hover:bg-primary/90 h-16"
                      data-testid="button-browse-problems"
                    >
                      <Play className="mr-2 h-5 w-5" />
                      <div className="text-left">
                        <div className="font-semibold">Browse Problems</div>
                        <div className="text-sm opacity-90">Find your next challenge</div>
                      </div>
                    </Button>
                  </Link>

                  <Link href="/community">
                    <Button 
                      variant="outline" 
                      className="w-full h-16"
                      data-testid="button-join-community"
                    >
                      <Users className="mr-2 h-5 w-5" />
                      <div className="text-left">
                        <div className="font-semibold">Join Community</div>
                        <div className="text-sm opacity-70">Share and learn together</div>
                      </div>
                    </Button>
                  </Link>
                </div>
              </CardContent>
            </Card>

            {/* Recent Problems */}
            <Card>
              <CardHeader>
                <div className="flex justify-between items-center">
                  <CardTitle>Recommended Problems</CardTitle>
                  <Link href="/problems">
                    <Button variant="ghost" size="sm" data-testid="link-view-all-problems">
                      View All
                    </Button>
                  </Link>
                </div>
              </CardHeader>
              <CardContent>
                {problemsLoading ? (
                  <div className="space-y-4">
                    {[...Array(3)].map((_, i) => (
                      <div key={i} className="h-20 bg-muted rounded-lg animate-pulse" />
                    ))}
                  </div>
                ) : (
                  <div className="space-y-4">
                    {recentProblems.map((problem) => (
                      <Link key={problem.id} href={`/problems/${problem.id}`}>
                        <Card className="hover:shadow-md transition-shadow cursor-pointer" data-testid={`card-problem-${problem.id}`}>
                          <CardContent className="p-4">
                            <div className="flex items-start justify-between">
                              <div className="flex-1">
                                <h3 className="font-semibold text-foreground mb-2">{problem.title}</h3>
                                <p className="text-sm text-muted-foreground line-clamp-2">
                                  {problem.description}
                                </p>
                              </div>
                              <div className="ml-4">
                                <DifficultyBadge
                                  difficulty={problem.difficulty}
                                  variant="badge"
                                  size="sm"
                                  showIcon={true}
                                  data-testid={`difficulty-badge-home-${problem.id}`}
                                />
                              </div>
                            </div>
                            <div className="flex items-center justify-between mt-3">
                              <div className="flex items-center space-x-4">
                                {problem.company && (
                                  <CompanyLogo
                                    companyName={problem.company}
                                    variant="minimal"
                                    size="sm"
                                    data-testid={`company-logo-home-${problem.id}`}
                                  />
                                )}
                                <span className="text-xs text-muted-foreground">
                                  {problem.solvedCount} solved
                                </span>
                              </div>
                              <Button size="sm" variant="ghost" className="text-primary">
                                Start Training â†’
                              </Button>
                            </div>
                          </CardContent>
                        </Card>
                      </Link>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            <HelpfulLinksSection />
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/ui/form.tsx.content:
"use client"

import * as React from "react"
import * as LabelPrimitive from "@radix-ui/react-label"
import { Slot } from "@radix-ui/react-slot"
import {
  Controller,
  FormProvider,
  useFormContext,
  type ControllerProps,
  type FieldPath,
  type FieldValues,
} from "react-hook-form"

import { cn } from "@/lib/utils"
import { Label } from "@/components/ui/label"

const Form = FormProvider

type FormFieldContextValue<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
> = {
  name: TName
}

const FormFieldContext = React.createContext<FormFieldContextValue>(
  {} as FormFieldContextValue
)

const FormField = <
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
>({
  ...props
}: ControllerProps<TFieldValues, TName>) => {
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      <Controller {...props} />
    </FormFieldContext.Provider>
  )
}

const useFormField = () => {
  const fieldContext = React.useContext(FormFieldContext)
  const itemContext = React.useContext(FormItemContext)
  const { getFieldState, formState } = useFormContext()

  const fieldState = getFieldState(fieldContext.name, formState)

  if (!fieldContext) {
    throw new Error("useFormField should be used within <FormField>")
  }

  const { id } = itemContext

  return {
    id,
    name: fieldContext.name,
    formItemId: `${id}-form-item`,
    formDescriptionId: `${id}-form-item-description`,
    formMessageId: `${id}-form-item-message`,
    ...fieldState,
  }
}

type FormItemContextValue = {
  id: string
}

const FormItemContext = React.createContext<FormItemContextValue>(
  {} as FormItemContextValue
)

const FormItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const id = React.useId()

  return (
    <FormItemContext.Provider value={{ id }}>
      <div ref={ref} className={cn("space-y-2", className)} {...props} />
    </FormItemContext.Provider>
  )
})
FormItem.displayName = "FormItem"

const FormLabel = React.forwardRef<
  React.ElementRef<typeof LabelPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof LabelPrimitive.Root>
>(({ className, ...props }, ref) => {
  const { error, formItemId } = useFormField()

  return (
    <Label
      ref={ref}
      className={cn(error && "text-destructive", className)}
      htmlFor={formItemId}
      {...props}
    />
  )
})
FormLabel.displayName = "FormLabel"

const FormControl = React.forwardRef<
  React.ElementRef<typeof Slot>,
  React.ComponentPropsWithoutRef<typeof Slot>
>(({ ...props }, ref) => {
  const { error, formItemId, formDescriptionId, formMessageId } = useFormField()

  return (
    <Slot
      ref={ref}
      id={formItemId}
      aria-describedby={
        !error
          ? `${formDescriptionId}`
          : `${formDescriptionId} ${formMessageId}`
      }
      aria-invalid={!!error}
      {...props}
    />
  )
})
FormControl.displayName = "FormControl"

const FormDescription = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, ...props }, ref) => {
  const { formDescriptionId } = useFormField()

  return (
    <p
      ref={ref}
      id={formDescriptionId}
      className={cn("text-sm text-muted-foreground", className)}
      {...props}
    />
  )
})
FormDescription.displayName = "FormDescription"

const FormMessage = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, children, ...props }, ref) => {
  const { error, formMessageId } = useFormField()
  const body = error ? String(error?.message ?? "") : children

  if (!body) {
    return null
  }

  return (
    <p
      ref={ref}
      id={formMessageId}
      className={cn("text-sm font-medium text-destructive", className)}
      {...props}
    >
      {body}
    </p>
  )
})
FormMessage.displayName = "FormMessage"

export {
  useFormField,
  Form,
  FormItem,
  FormLabel,
  FormControl,
  FormDescription,
  FormMessage,
  FormField,
}

scripts/generate-logos.js.content:
import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const LOGOS_DIR = path.join(__dirname, '..', 'attached_assets', 'logos');
const OUTPUT_FILE = path.join(__dirname, '..', 'client', 'src', 'data', 'companyLogos.ts');

const COMPANY_COLORS = {
  microsoft: {
    primaryColor: '#00BCF2',
    secondaryColor: '#0078D4',
  },
  google: {
    primaryColor: '#4285F4',
    secondaryColor: '#DB4437',
  },
  apple: {
    primaryColor: '#000000',
    secondaryColor: '#A8A8A8',
  },
  amazon: {
    primaryColor: '#FF9900',
    secondaryColor: '#232F3E',
  },
  meta: {
    primaryColor: '#1877F2',
    secondaryColor: '#42B883',
  },
  netflix: {
    primaryColor: '#E50914',
    secondaryColor: '#221F1F',
  },
  stripe: {
    primaryColor: '#635BFF',
    secondaryColor: '#0A2540',
  },
  airbnb: {
    primaryColor: '#FF5A5F',
    secondaryColor: '#FF385C',
  },
  tesla: {
    primaryColor: '#CC0000',
    secondaryColor: '#000000',
  },
  uber: {
    primaryColor: '#000000',
    secondaryColor: '#1FBAD6',
  },
  shopify: {
    primaryColor: '#7AB55C',
    secondaryColor: '#95BF47',
  },
  discord: {
    primaryColor: '#5865F2',
    secondaryColor: '#7289DA',
  },
  slack: {
    primaryColor: '#4A154B',
    secondaryColor: '#36C5F0',
  },
  snapchat: {
    primaryColor: '#FFFC00',
    secondaryColor: '#000000',
  },
  mcdonald: {
    primaryColor: '#FFC72C',
    secondaryColor: '#DA291C',
  },
};

function normalizeCompanyName(filename) {
  return filename
    .replace(/\.svg$/i, '')
    .replace(/[,_.\-\s]/g, '')
    .toLowerCase();
}

function toValidVarName(filename) {
  return filename
    .replace(/\.svg$/i, '')
    .replace(/[^a-zA-Z0-9]/g, '')
    .toLowerCase();
}

function generateLogosFile() {
  if (!fs.existsSync(LOGOS_DIR)) {
    console.error(`Error: Logos directory not found at ${LOGOS_DIR}`);
    process.exit(1);
  }

  const logoFiles = fs.readdirSync(LOGOS_DIR).filter(file => file.endsWith('.svg'));

  if (logoFiles.length === 0) {
    console.warn('Warning: No SVG files found in logos directory');
    return;
  }

  const imports = [];
  const registry = [];

  logoFiles.forEach(file => {
    const normalizedName = normalizeCompanyName(file);
    const validVarName = toValidVarName(file);
    const varName = `${validVarName}Logo`;

    imports.push(`import ${varName} from '@assets/logos/${file}';`);
    registry.push(`  ${normalizedName}: ${varName},`);
  });

  const colorsJson = JSON.stringify(COMPANY_COLORS, null, 2)
    .replace(/"([^"]+)":/g, '$1:');

  const output = `/**
 * AUTO-GENERATED FILE - DO NOT EDIT MANUALLY
 * Generated by scripts/generate-logos.js
 * Run 'npm run generate:logos' to regenerate
 */

${imports.join('\n')}

export interface CompanyInfo {
  id: string;
  name: string;
  displayName: string;
  logoPath: string;
  primaryColor: string;
  secondaryColor?: string;
}

// Auto-generated logo registry
const LOGO_REGISTRY: Record<string, string> = {
${registry.join('\n')}
};

// Default color configurations for known companies
const COMPANY_COLORS: Record<string, Pick<CompanyInfo, 'primaryColor' | 'secondaryColor'>> = ${colorsJson};

/**
 * Normalizes company name to match expected filename format
 */
function normalizeCompanyName(name: string): string {
  return name
    .replace(/[,_-]/g, '')
    .replace(/\\s+/g, '')
    .toLowerCase();
}

/**
 * Retrieves company logo information by name
 */
export function getCompanyLogo(companyName: string): CompanyInfo | null {
  const normalized = normalizeCompanyName(companyName);
  const logoPath = LOGO_REGISTRY[normalized];

  if (!logoPath) {
    return null;
  }

  const colors = COMPANY_COLORS[normalized] || {
    primaryColor: '#000000',
    secondaryColor: '#666666',
  };

  return {
    id: normalized,
    name: normalized,
    displayName: companyName,
    logoPath,
    ...colors,
  };
}

/**
 * Returns all available company logos
 */
export function getAllCompanyLogos(): CompanyInfo[] {
  return Object.keys(LOGO_REGISTRY).map(key => ({
    id: key,
    name: key,
    displayName: key.charAt(0).toUpperCase() + key.slice(1),
    logoPath: LOGO_REGISTRY[key],
    ...(COMPANY_COLORS[key] || {
      primaryColor: '#000000',
      secondaryColor: '#666666',
    }),
  }));
}

export { LOGO_REGISTRY, COMPANY_COLORS };
`;

  fs.writeFileSync(OUTPUT_FILE, output, 'utf-8');
  console.log(`âœ… Successfully generated ${OUTPUT_FILE}`);
  console.log(`ðŸ“Š Processed ${logoFiles.length} logo files`);
}

generateLogosFile();

client/src/pages/submissions.tsx.content:
import { useQuery } from '@tanstack/react-query';
import { Clock, CheckCircle, XCircle, Code, ArrowRight, Filter } from 'lucide-react';
import { useState } from 'react';
import { Link } from 'wouter';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { submissionsApi, problemsApi } from '@/lib/auth';
import { useAuth } from '@/hooks/use-auth';

export default function Submissions() {
  const [filter, setFilter] = useState<'all' | 'correct' | 'incorrect'>('all');
  const { user } = useAuth();

  const { data: submissions, isLoading: submissionsLoading } = useQuery({
    queryKey: ['/api/submissions/user', user?.id],
    queryFn: () => submissionsApi.getUserSubmissions(user!.id),
    enabled: !!user?.id,
  });

  const { data: problems } = useQuery({
    queryKey: ['/api/problems'],
    queryFn: () => problemsApi.getAll(),
  });

  const filteredSubmissions = submissions?.filter(submission => {
    if (filter === 'correct') return submission.isCorrect;
    if (filter === 'incorrect') return !submission.isCorrect;
    return true;
  }) || [];

  const getProblemTitle = (problemId: string) => {
    return problems?.find(p => p.id === problemId)?.title || 'Unknown Problem';
  };

  const getProblemDifficulty = (problemId: string) => {
    return problems?.find(p => p.id === problemId)?.difficulty || 'Unknown';
  };

  const getDifficultyColor = (difficulty: string) => {
    switch (difficulty) {
      case 'Easy': return 'bg-green-100 text-green-800';
      case 'Medium': return 'bg-yellow-100 text-yellow-800';
      case 'Hard': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const truncateQuery = (query: string, maxLength: number = 100) => {
    if (query.length <= maxLength) return query;
    return query.substring(0, maxLength).trim() + '...';
  };

  // Calculate stats
  const totalSubmissions = submissions?.length || 0;
  const correctSubmissions = submissions?.filter(s => s.isCorrect).length || 0;
  const successRate = totalSubmissions > 0 ? Math.round((correctSubmissions / totalSubmissions) * 100) : 0;
  const avgExecutionTime = submissions?.length ? 
    Math.round(submissions.reduce((sum, s) => sum + (s.executionTime || 0), 0) / submissions.length) : 0;

  return (
    <div className="min-h-screen bg-background">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-foreground mb-4">Your Submissions</h1>
          <p className="text-xl text-muted-foreground">Track your SQL training progress and achievements</p>
        </div>

        {/* Stats Overview */}
        <div className="grid md:grid-cols-4 gap-6 mb-8">
          <Card>
            <CardContent className="p-6 text-center">
              <div className="text-3xl font-bold text-primary mb-2" data-testid="stat-total-submissions">
                {totalSubmissions}
              </div>
              <div className="text-sm text-muted-foreground">Total Submissions</div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6 text-center">
              <div className="text-3xl font-bold text-green-600 mb-2" data-testid="stat-correct-submissions">
                {correctSubmissions}
              </div>
              <div className="text-sm text-muted-foreground">Correct Solutions</div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6 text-center">
              <div className="text-3xl font-bold text-blue-600 mb-2" data-testid="stat-success-rate">
                {successRate}%
              </div>
              <div className="text-sm text-muted-foreground">Success Rate</div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6 text-center">
              <div className="text-3xl font-bold text-orange-600 mb-2" data-testid="stat-avg-time">
                {avgExecutionTime}ms
              </div>
              <div className="text-sm text-muted-foreground">Avg Execution Time</div>
            </CardContent>
          </Card>
        </div>

        {/* Filters */}
        <div className="flex items-center space-x-4 mb-6">
          <div className="flex items-center space-x-2">
            <Filter className="w-4 h-4 text-muted-foreground" />
            <span className="text-sm font-medium text-foreground">Filter:</span>
          </div>
          <div className="bg-white border border-border rounded-lg p-1 inline-flex">
            <Button
              variant={filter === 'all' ? 'default' : 'ghost'}
              size="sm"
              onClick={() => setFilter('all')}
              className={filter === 'all' ? 'bg-primary text-primary-foreground' : ''}
              data-testid="button-filter-all"
            >
              All ({totalSubmissions})
            </Button>
            <Button
              variant={filter === 'correct' ? 'default' : 'ghost'}
              size="sm"
              onClick={() => setFilter('correct')}
              className={filter === 'correct' ? 'bg-primary text-primary-foreground' : ''}
              data-testid="button-filter-correct"
            >
              Correct ({correctSubmissions})
            </Button>
            <Button
              variant={filter === 'incorrect' ? 'default' : 'ghost'}
              size="sm"
              onClick={() => setFilter('incorrect')}
              className={filter === 'incorrect' ? 'bg-primary text-primary-foreground' : ''}
              data-testid="button-filter-incorrect"
            >
              Incorrect ({totalSubmissions - correctSubmissions})
            </Button>
          </div>
        </div>

        {/* Submissions List */}
        {submissionsLoading ? (
          <div className="space-y-4">
            {[...Array(5)].map((_, i) => (
              <Card key={i} className="animate-pulse">
                <CardContent className="p-6">
                  <div className="flex items-start justify-between">
                    <div className="space-y-3 flex-1">
                      <div className="h-4 bg-muted rounded w-1/3" />
                      <div className="h-3 bg-muted rounded w-1/2" />
                      <div className="h-16 bg-muted rounded" />
                    </div>
                    <div className="ml-4 space-y-2">
                      <div className="h-6 bg-muted rounded w-20" />
                      <div className="h-4 bg-muted rounded w-16" />
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : filteredSubmissions.length === 0 ? (
          <Card>
            <CardContent className="p-12 text-center">
              <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mx-auto mb-4">
                <Code className="w-8 h-8 text-muted-foreground" />
              </div>
              <h3 className="text-lg font-semibold text-foreground mb-2">
                {filter === 'all' ? 'No submissions yet' : `No ${filter} submissions`}
              </h3>
              <p className="text-muted-foreground mb-6">
                {filter === 'all' 
                  ? 'Start solving problems to see your submission history here!'
                  : `You haven't made any ${filter} submissions yet.`}
              </p>
              {filter === 'all' && (
                <Link href="/problems">
                  <Button className="dumbbell-btn bg-primary text-primary-foreground hover:bg-primary/90" data-testid="button-start-solving">
                    Start Solving Problems
                  </Button>
                </Link>
              )}
            </CardContent>
          </Card>
        ) : (
          <div className="space-y-4">
            {filteredSubmissions.map((submission, index) => (
              <Card key={submission.id} className="hover:shadow-md transition-shadow" data-testid={`submission-${index}`}>
                <CardContent className="p-6">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      {/* Header */}
                      <div className="flex items-center space-x-3 mb-3">
                        <div className={`flex items-center justify-center w-8 h-8 rounded-full ${
                          submission.isCorrect ? 'bg-green-100' : 'bg-red-100'
                        }`}>
                          {submission.isCorrect ? (
                            <CheckCircle className="w-5 h-5 text-green-600" />
                          ) : (
                            <XCircle className="w-5 h-5 text-red-600" />
                          )}
                        </div>

                        <div className="flex-1">
                          <Link href={`/problems/${submission.problemId}`}>
                            <h3 className="font-semibold text-foreground hover:text-primary transition-colors cursor-pointer" data-testid={`text-problem-title-${index}`}>
                              {getProblemTitle(submission.problemId)}
                            </h3>
                          </Link>
                          <div className="flex items-center space-x-3 mt-1">
                            <Badge className={getDifficultyColor(getProblemDifficulty(submission.problemId))}>
                              {getProblemDifficulty(submission.problemId)}
                            </Badge>
                            <div className="flex items-center space-x-1 text-sm text-muted-foreground">
                              <Clock className="w-3 h-3" />
                              <span data-testid={`text-submission-date-${index}`}>
                                {formatDate(submission.submittedAt)}
                              </span>
                            </div>
                            {submission.executionTime && (
                              <span className="text-sm text-muted-foreground" data-testid={`text-execution-time-${index}`}>
                                {submission.executionTime}ms
                              </span>
                            )}
                          </div>
                        </div>
                      </div>

                      {/* Query Preview */}
                      <div className="bg-muted rounded-lg p-3 mb-3">
                        <div className="flex items-center justify-between mb-2">
                          <span className="text-sm font-medium text-foreground">SQL Query</span>
                          <Badge variant="outline" className={
                            submission.isCorrect ? 'text-green-700 border-green-300' : 'text-red-700 border-red-300'
                          }>
                            {submission.isCorrect ? 'Correct' : 'Incorrect'}
                          </Badge>
                        </div>
                        <pre className="text-sm text-muted-foreground font-mono overflow-x-auto">
                          <code data-testid={`code-query-${index}`}>
                            {truncateQuery(submission.query)}
                          </code>
                        </pre>
                        {submission.query.length > 100 && (
                          <Button 
                            variant="ghost" 
                            size="sm" 
                            className="mt-2 text-primary hover:bg-primary/10"
                            data-testid={`button-view-full-${index}`}
                          >
                            View Full Query
                          </Button>
                        )}
                      </div>
                    </div>

                    {/* Actions */}
                    <div className="ml-4 flex flex-col space-y-2">
                      <Link href={`/problems/${submission.problemId}`}>
                        <Button 
                          variant="outline" 
                          size="sm"
                          className="w-full"
                          data-testid={`button-view-problem-${index}`}
                        >
                          View Problem
                          <ArrowRight className="ml-2 w-3 h-3" />
                        </Button>
                      </Link>

                      {!submission.isCorrect && (
                        <Link href={`/problems/${submission.problemId}`}>
                          <Button 
                            variant="outline" 
                            size="sm"
                            className="w-full text-primary border-primary hover:bg-primary/10"
                            data-testid={`button-retry-${index}`}
                          >
                            Try Again
                          </Button>
                        </Link>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

