}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased text-foreground;
    background-color: #FFFFFF !important;
  }
}

.vintage-overlay {
  background-image: url('https://www.transparenttextures.com/patterns/noisy-grid.png');
  opacity: 0.15;
  mix-blend-mode: multiply;
}

/* Gym-inspired custom styles */
.dumbbell-btn {
  position: relative;
  transition: all 0.3s ease;
}

.dumbbell-btn:hover {
  transform: translateY(-2px);
}

.progress-weight {
  background: linear-gradient(90deg, var(--gym-red) 0%, var(--gym-steel) 100%);
  animation: fillProgress 2s ease-in-out;
  transition: width 0.3s ease;
}

@keyframes fillProgress {
  from {
    width: 0%;
  }

  to {
    width: var(--progress-width);
  }
}

.weight-plate {
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background: var(--primary);
  position: relative;
}

.weight-plate::before {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 6px;
  height: 6px;
  border-radius: 50%;
  background: white;
}

.syntax-highlight {
  font-family: var(--font-mono);
  background: var(--muted);
  padding: 1rem;
  border-radius: 12px;
  border-left: 4px solid var(--primary);
  overflow-x: auto;
  /* Adds horizontal scroll for very long content */
  max-width: 100%;
  /* Ensures it doesn't break container */
}

.syntax-highlight pre {
  white-space: pre-wrap;
  /* Allows wrapping while preserving formatting */
  overflow-wrap: break-word;
  /* Breaks long words if needed */
  word-break: break-all;
  /* Breaks long SQL statements at any character */
  margin: 0;
  /* Remove default pre margin */
}

.code-editor {
  font-family: var(--font-mono);
  background: hsl(222 47% 11%);
  color: hsl(210 40% 98%);
}

/* Smooth scrolling */
html {
  scroll-behavior: smooth;
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: var(--muted);
}

::-webkit-scrollbar-thumb {
  background: var(--border);
  border-radius: 8px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--muted-foreground);
}

/* Console output styling */
.console-output {
  background: #1a1a1a;
  color: #e0e0e0;
  font-family: 'JetBrains Mono', 'Fira Code', 'Monaco', 'Menlo', 'Ubuntu Mono', monospace;
  font-size: 13px;
  line-height: 1.4;
  padding: 16px;
  border-radius: 12px;
  white-space: pre-wrap;
  overflow-x: auto;
  border: 1px solid #333;
}

.console-output.error {
  color: #ff6b6b;
  background: #2d1b1b;
  border-color: #8b0000;
}

.console-output.success {
  border-left: 4px solid #28ca42;
}

/* Make sure pre elements don't have conflicting styles */
pre.console-output {
  margin: 0;
  font-family: inherit;
}

/* GitHub-style Calendar Heatmap Styles */
.react-calendar-heatmap {
  font-size: 12px;
}

.react-calendar-heatmap .react-calendar-heatmap-month-label {
  font-size: 11px;
  fill: var(--muted-foreground);
}

.react-calendar-heatmap .react-calendar-heatmap-weekday-label {
  font-size: 10px;
  fill: var(--muted-foreground);
}

.react-calendar-heatmap rect {
  rx: 2;
}

/* Light mode colors */
:root .react-calendar-heatmap .color-empty {
  fill: #ebedf0;
}

:root .react-calendar-heatmap .color-scale-1 {
