"use client"

import * as React from "react"
import * as DialogPrimitive from "@radix-ui/react-dialog"
import { X } from "lucide-react"

import { cn } from "@/lib/utils"

const Dialog = DialogPrimitive.Root

const DialogTrigger = DialogPrimitive.Trigger

const DialogPortal = DialogPrimitive.Portal

const DialogClose = DialogPrimitive.Close

const DialogOverlay = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Overlay>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Overlay>
>(({ className, ...props }, ref) => (
  <DialogPrimitive.Overlay
    ref={ref}
    className={cn(
      "fixed inset-0 z-50 bg-black/80 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0",
      className
    )}
    {...props}
  />
))
DialogOverlay.displayName = DialogPrimitive.Overlay.displayName

const DialogContent = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Content>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Content>
>(({ className, children, ...props }, ref) => (
  <DialogPortal>
    <DialogOverlay />
    <DialogPrimitive.Content
      ref={ref}
      className={cn(
        "fixed left-[50%] top-[50%] z-50 grid w-full max-w-lg translate-x-[-50%] translate-y-[-50%] gap-4 border bg-background p-6 shadow-lg duration-200 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[state=closed]:slide-out-to-left-1/2 data-[state=closed]:slide-out-to-top-[48%] data-[state=open]:slide-in-from-left-1/2 data-[state=open]:slide-in-from-top-[48%] sm:rounded-lg",
        className
      )}
      {...props}
    >
      {children}
      <DialogPrimitive.Close className="absolute right-4 top-4 rounded-sm opacity-70 ring-offset-background transition-opacity hover:opacity-100 focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 disabled:pointer-events-none data-[state=open]:bg-accent data-[state=open]:text-muted-foreground">
        <X className="h-4 w-4" />
        <span className="sr-only">Close</span>
      </DialogPrimitive.Close>
    </DialogPrimitive.Content>
  </DialogPortal>
))
DialogContent.displayName = DialogPrimitive.Content.displayName

const DialogHeader = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col space-y-1.5 text-center sm:text-left",
      className
    )}
    {...props}
  />
)
DialogHeader.displayName = "DialogHeader"

const DialogFooter = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col-reverse sm:flex-row sm:justify-end sm:space-x-2",
      className
    )}
    {...props}
  />
)
DialogFooter.displayName = "DialogFooter"

const DialogTitle = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Title>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Title>
>(({ className, ...props }, ref) => (
  <DialogPrimitive.Title
    ref={ref}
    className={cn(
      "text-lg font-semibold leading-none tracking-tight",
      className
    )}
    {...props}
  />
))
DialogTitle.displayName = DialogPrimitive.Title.displayName

const DialogDescription = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Description>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Description>
>(({ className, ...props }, ref) => (
  <DialogPrimitive.Description
    ref={ref}
    className={cn("text-sm text-muted-foreground", className)}
    {...props}
  />
))
DialogDescription.displayName = DialogPrimitive.Description.displayName

export {
  Dialog,
  DialogPortal,
  DialogOverlay,
  DialogClose,
  DialogTrigger,
  DialogContent,
  DialogHeader,
  DialogFooter,
  DialogTitle,
  DialogDescription,
}

client/src/components/SubmissionResultPa.content:
interface SubmissionResult {
  success: boolean;
  is_correct: boolean;
  score: number;
  feedback: string[];
  test_results: TestResult[];
  submission_id: string;
  execution_stats: {
    avg_time_ms: number;
    max_time_ms: number;
    memory_used_mb: number;
  };
  user_query?: string;
}

interface AIHint {
  issue_identified: string;
  concept_needed: string;
  hint: string;
  confidence: number;
}

interface TestResult {
  test_case_id: string;
  test_case_name: string;
  is_hidden: boolean;
  is_correct: boolean;
  score: number;
  feedback: string[];
  execution_time_ms: number;
  execution_status: string;
  validation_details: any;
  user_output: any[];
  expected_output: any[];
  output_matches: boolean;
}

interface SubmissionResultPanelProps {
  result: SubmissionResult | null;
  isLoading: boolean;
  problemId: string;
  userQuery?: string;
}

import { useState, useEffect } from "react";
import { Lightbulb, Loader2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { apiRequest } from "@/lib/queryClient";

export default function SubmissionResultPanel({ result, isLoading, problemId, userQuery }: SubmissionResultPanelProps) {
  const [aiHint, setAiHint] = useState<AIHint | null>(null);
  const [isLoadingHint, setIsLoadingHint] = useState(false);
  const [hintError, setHintError] = useState<string | null>(null);

  // Reset hint when submission changes
  useEffect(() => {
    setAiHint(null);
    setHintError(null);
  }, [result?.submission_id]);

  const handleGetAIHint = async () => {
    if (!result || !userQuery) return;

    setIsLoadingHint(true);
    setHintError(null);

    try {
      const mainTestResult = result.test_results?.find(test => !test.is_hidden) || result.test_results?.[0];

      const response = await apiRequest(
        "POST",
        `/api/problems/${problemId}/ai-hint`,
        {
          user_query: userQuery,
          feedback: result.feedback || [],
          user_output: mainTestResult?.user_output?.slice(0, 3),
          expected_output: mainTestResult?.expected_output?.slice(0, 3)
        }
      );

      const data = await response.json();

      if (data.success && data.hint) {
        setAiHint(data.hint);
      } else {
        setHintError("Unable to generate hint. Please try again.");
      }
    } catch (error: any) {
      console.error("Error getting AI hint:", error);
      setHintError(error.message || "Failed to get hint. Please try again.");
    } finally {
      setIsLoadingHint(false);
    }
  };

  if (isLoading) {
    return (
      <div className="h-full bg-gray-50 flex items-center justify-center">
        <div className="text-gray-600">Submitting solution...</div>
      </div>
    );
  }

  if (!result) {
    return (
      <div className="h-full bg-gray-50 flex items-center justify-center">
        <div className="text-gray-500">Submit your solution to see results...</div>
      </div>
    );
  }

  // Get the first non-hidden test result for main comparison display
  const mainTestResult = result.test_results?.find(test => !test.is_hidden) || result.test_results?.[0];
  const hasOutputMismatch = !result.is_correct && mainTestResult;
  const hasNoTestCases = !result.test_results || result.test_results.length === 0;

  return (
    <div className="h-full bg-gray-50 flex flex-col overflow-auto">
      <div className="p-4 space-y-4 flex-1 min-h-0">
        {/* Mismatch Banner */}
        {hasOutputMismatch && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-3" data-testid="banner-mismatch">
            <div className="flex items-center justify-between">
              <div>
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-red-500 rounded-full"></div>
                  <span className="text-red-800 font-medium text-sm">Mismatched</span>
                </div>
                <p className="text-red-700 text-sm mt-1">
                  Your query's output doesn't match with the solution's output!
                </p>
              </div>
              {!aiHint && (
                <Button
                  onClick={handleGetAIHint}
                  disabled={isLoadingHint}
                  variant="outline"
                  size="sm"
                  className="bg-white hover:bg-gray-50"
                  data-testid="button-get-hint"
                >
                  {isLoadingHint ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Getting hint...
                    </>
                  ) : (
                    <>
                      <Lightbulb className="w-4 h-4 mr-2" />
                      Get AI Hint
                    </>
                  )}
                </Button>
              )}
            </div>
          </div>
        )}

        {/* Success Banner */}
        {result.is_correct && (
          <div className="bg-green-50 border border-green-200 rounded-lg p-3" data-testid="banner-success">
            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 bg-green-500 rounded-full"></div>
              <span className="text-green-800 font-medium text-sm">Success!</span>
            </div>
            <p className="text-green-700 text-sm mt-1">
              Your solution is correct! Well done!
            </p>
          </div>
        )}

        {/* Submission Stats */}
        <div className="bg-white border border-gray-200 rounded-lg p-4">
          <div className="grid grid-cols-3 gap-4 text-center">
            <div>
              <div className="text-lg font-semibold text-gray-900">
                {(result.score ?? 0).toFixed(1)}%
              </div>
              <div className="text-xs text-gray-500">Score</div>
            </div>
            <div>
              <div className="text-lg font-semibold text-gray-900">
                {result.execution_stats?.avg_time_ms ?? 0}ms
              </div>
              <div className="text-xs text-gray-500">Runtime</div>
            </div>
            <div>
              <div className="text-lg font-semibold text-gray-900">
                {(result.execution_stats?.memory_used_mb ?? 0).toFixed(1)}MB
              </div>
              <div className="text-xs text-gray-500">Memory</div>
            </div>
          </div>
        </div>

        {/* AI Hint Display */}
        {aiHint && (
          <div className="bg-gradient-to-br from-teal-50 to-cyan-50 border border-teal-200 rounded-lg p-4" data-testid="ai-hint-panel">
            <div className="flex items-start space-x-3">
              <div className="bg-teal-500 rounded-full p-2 mt-0.5">
                <Lightbulb className="w-4 h-4 text-white" />
              </div>
              <div className="flex-1">
                <h4 className="text-sm font-semibold text-teal-900 mb-3">AI Hint</h4>

                <div className="space-y-3">
                  <div>
                    <span className="text-xs font-medium text-teal-700 uppercase tracking-wide">Issue Identified</span>
                    <p className="text-sm text-teal-800 mt-1">{aiHint.issue_identified}</p>
                  </div>

                  <div>
                    <span className="text-xs font-medium text-teal-700 uppercase tracking-wide">Concept to Use</span>
                    <p className="text-sm text-teal-800 mt-1 font-medium">{aiHint.concept_needed}</p>
                  </div>

                  <div className="bg-white bg-opacity-70 rounded-md p-3">
                    <span className="text-xs font-medium text-teal-700 uppercase tracking-wide">ðŸ’¡ Hint</span>
                    <p className="text-sm text-teal-900 mt-1 leading-relaxed">{aiHint.hint}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Hint Error Display */}
        {hintError && (
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3">
            <p className="text-sm text-yellow-800">{hintError}</p>
          </div>
        )}

        {/* Show message when no test cases are available */}
        {hasNoTestCases && (
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 bg-yellow-500 rounded-full"></div>
              <span className="text-yellow-800 font-medium text-sm">No Test Cases Available</span>
            </div>
            <p className="text-yellow-700 text-sm mt-1">
              This problem doesn't have test cases configured yet. Your query was executed successfully, but we can't compare the results against expected outputs.
            </p>
          </div>
        )}

      </div>
    </div>
  );
}

