import { useMemo } from 'react';

interface RowComparison {
  row_index: number;
  matches: boolean;
  actual_row: Record<string, any> | null;
  expected_row: Record<string, any>;
  differences?: string;
}

interface ValidationDetails {
  row_comparisons?: RowComparison[];
  matching_row_count?: number;
  total_row_count?: number;
  comparison_differences?: string[];
}

interface ResultComparisonTableProps {
  validationDetails: ValidationDetails;
  isCorrect: boolean;
}

export default function ResultComparisonTable({ validationDetails, isCorrect }: ResultComparisonTableProps) {
  const { headers, comparisons } = useMemo(() => {
    const rowComparisons = validationDetails.row_comparisons || [];

    if (rowComparisons.length === 0) {
      return { headers: [], comparisons: [] };
    }

    // Get headers from the first expected row
    const firstExpected = rowComparisons[0]?.expected_row;
    const headers = firstExpected ? Object.keys(firstExpected) : [];

    return { headers, comparisons: rowComparisons };
  }, [validationDetails]);

  if (!validationDetails.row_comparisons || validationDetails.row_comparisons.length === 0) {
    return null;
  }

  const matchingCount = validationDetails.matching_row_count || 0;
  const totalCount = validationDetails.total_row_count || 0;

  return (
    <div className="bg-white border border-gray-200 rounded-lg p-4" data-testid="comparison-table">
      <div className="mb-4">
        <h4 className="text-sm font-medium text-gray-900 mb-2">Detailed Result Comparison</h4>
        <div className="text-xs text-gray-600 mb-2">
          {matchingCount} of {totalCount} rows match
        </div>

        {/* Legend */}
        <div className="flex items-center space-x-4 text-xs mb-3">
          <div className="flex items-center space-x-1">
            <div className="w-3 h-3 bg-green-100 border border-green-200 rounded"></div>
            <span className="text-gray-600">Matching rows</span>
          </div>
          <div className="flex items-center space-x-1">
            <div className="w-3 h-3 bg-white border border-gray-200 rounded"></div>
            <span className="text-gray-600">Non-matching rows</span>
          </div>
        </div>
      </div>

      <div className="overflow-x-auto max-h-96">
        <table className="w-full border-collapse">
          <thead className="sticky top-0 bg-gray-50">
            <tr>
              <th className="px-3 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider border-b border-gray-200">
                Row
              </th>
              <th className="px-3 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider border-b border-gray-200">
                Status
              </th>
              {headers.map((header, i) => (
                <th 
                  key={i} 
                  className="px-3 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider border-b border-gray-200"
                >
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {comparisons.map((comparison) => {
              const rowClass = comparison.matches 
                ? 'bg-green-50 hover:bg-green-100' 
                : 'bg-white hover:bg-gray-50';

              return (
                <tr 
                  key={comparison.row_index} 
                  className={rowClass}
                  data-testid={`row-comparison-${comparison.row_index}`}
                >
                  <td className="px-3 py-2 text-sm text-gray-900 font-medium">
                    {comparison.row_index + 1}
                  </td>
                  <td className="px-3 py-2 text-sm">
                    {comparison.matches ? (
                      <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                        âœ“ Match
                      </span>
                    ) : (
                      <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-red-100 text-red-800">
                        âœ— Different
                      </span>
                    )}
                  </td>
                  {headers.map((header, i) => {
                    const expectedValue = comparison.expected_row[header];
                    const actualValue = comparison.actual_row?.[header];
                    const cellMatches = comparison.matches || (expectedValue === actualValue);

                    return (
                      <td 
                        key={i} 
                        className={`px-3 py-2 text-sm ${
                          cellMatches ? 'text-gray-900' : 'text-red-700 font-medium'
                        }`}
                      >
                        <div className="flex flex-col space-y-1">
                          {/* Show expected value */}
                          <div className="text-xs text-gray-500">
                            Expected: {String(expectedValue ?? '')}
                          </div>
                          {/* Show actual value if different or if row doesn't match */}
                          {(!comparison.matches || actualValue !== expectedValue) && (
                            <div className={`text-xs ${cellMatches ? 'text-gray-700' : 'text-red-600 font-medium'}`}>
                              Got: {actualValue !== undefined ? String(actualValue ?? '') : 'N/A'}
                            </div>
                          )}
                        </div>
                      </td>
                    );
                  })}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {/* Show comparison differences if any */}
      {validationDetails.comparison_differences && validationDetails.comparison_differences.length > 0 && (
        <div className="mt-4 p-3 bg-yellow-50 border border-yellow-200 rounded">
          <h5 className="text-sm font-medium text-yellow-800 mb-1">Additional Issues:</h5>
          <ul className="text-sm text-yellow-700 space-y-1">
            {validationDetails.comparison_differences.map((diff, index) => (
              <li key={index}>â€¢ {diff}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

client/src/components/admin/SolutionsTab.content:
import { useState, useEffect } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Save, FileText, Plus } from 'lucide-react';
import { useAdmin } from '@/contexts/AdminContext';
import { useToast } from '@/hooks/use-toast';
import { queryClient } from '@/lib/queryClient';

interface Solution {
  id: string;
  problem_id: string;
  title: string;
  content: string;
  sql_code: string;
  is_official: boolean;
  created_at: string;
  creator: {
    id: string;
    username: string;
  };
}

interface SolutionForm {
  title: string;
  content: string;
  sql_code: string;
}

export function SolutionsTab() {
  const { state } = useAdmin();
  const { toast } = useToast();
  const [selectedProblemId, setSelectedProblemId] = useState('');
  const [solutionForm, setSolutionForm] = useState<SolutionForm>({
    title: '',
    content: '',
    sql_code: ''
  });

  // React Query for problems
  const { data: problems = [], isLoading: problemsLoading } = useQuery({
    queryKey: ['/api/problems'],
    enabled: state.isAuthenticated
  });

  // React Query for existing solution (new endpoint)
  const { data: existingSolution, isLoading: solutionLoading, error: solutionError } = useQuery({
    queryKey: ['/api/admin/problems', selectedProblemId, 'solution'],
    enabled: !!selectedProblemId && state.isAuthenticated,
    queryFn: async () => {
      const authToken = localStorage.getItem('auth_token');
      const response = await fetch(`/api/admin/problems/${selectedProblemId}/solution`, {
        headers: {
          'Authorization': `Bearer ${authToken}`,
          'X-Admin-Session': state.adminKey
        }
      });
      if (!response.ok) {
        if (response.status === 404) {
          return null; // No solution exists yet
        }
        throw new Error('Failed to fetch solution');
      }
      return response.json();
    }
  });

  // Create or update solution mutation
  const saveSolutionMutation = useMutation({
    mutationFn: async (solutionData: SolutionForm) => {
      const authToken = localStorage.getItem('auth_token');
      const response = await fetch(`/api/admin/problems/${selectedProblemId}/solutions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${authToken}`,
          'X-Admin-Session': state.adminKey
        },
        body: JSON.stringify(solutionData)
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.detail || 'Failed to save solution');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: existingSolution ? "Solution updated successfully" : "Solution created successfully"
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/problems', selectedProblemId, 'solution'] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive"
      });
    }
  });

  const handleSubmit = () => {
    if (!selectedProblemId) {
      toast({
        title: "Error",
        description: "Please select a problem first",
        variant: "destructive"
      });
      return;
    }

    if (!solutionForm.title.trim() || !solutionForm.sql_code.trim()) {
      toast({
        title: "Error",
        description: "Please fill in the title and SQL code",
        variant: "destructive"
      });
      return;
    }

    saveSolutionMutation.mutate(solutionForm);
  };

  // Load existing solution data when problem changes
  useEffect(() => {
    if (existingSolution) {
      setSolutionForm({
        title: existingSolution.title || '',
        content: existingSolution.content || '',
        sql_code: existingSolution.sql_code || ''
      });
    } else {
      setSolutionForm({
        title: '',
        content: '',
        sql_code: ''
      });
    }
  }, [existingSolution, selectedProblemId]);

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle>Solution Management</CardTitle>
          <p className="text-sm text-muted-foreground">
            Create or edit solutions for problems. Each problem has one solution.
          </p>
        </CardHeader>
      </Card>

      {/* Problem Selection */}
      <Card>
        <CardHeader>
          <CardTitle>Select Problem</CardTitle>
        </CardHeader>
        <CardContent>
          <div>
            <Label htmlFor="problem-select">Choose a Problem</Label>
            <select
              id="problem-select"
              value={selectedProblemId}
              onChange={(e) => setSelectedProblemId(e.target.value)}
              className="w-full p-2 border rounded-md"
              data-testid="select-problem"
            >
              <option value="">Choose a problem...</option>
              {problems.map((problem: any) => (
                <option key={problem.id} value={problem.id}>
                  {problem.title} ({problem.difficulty})
                </option>
              ))}
            </select>
          </div>
        </CardContent>
      </Card>

      {/* Solution Form */}
      {selectedProblemId && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              {existingSolution ? (
                <>
                  <FileText className="w-5 h-5" />
                  Edit Solution
                </>
              ) : (
                <>
                  <Plus className="w-5 h-5" />
                  Create Solution
                </>
              )}
            </CardTitle>
            {solutionLoading && (
              <p className="text-sm text-muted-foreground">Loading solution...</p>
            )}
            {existingSolution && (
              <Alert>
                <AlertDescription>
                  Editing existing solution for this problem.
                </AlertDescription>
              </Alert>
            )}
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="solution-title">Solution Title</Label>
              <Input
                id="solution-title"
                value={solutionForm.title}
                onChange={(e) => setSolutionForm(prev => ({ ...prev, title: e.target.value }))}
                placeholder="e.g., Optimized JOIN solution"
                data-testid="input-solution-title"
              />
            </div>

            <div>
              <Label htmlFor="solution-content">Solution Explanation</Label>
              <Textarea
                id="solution-content"
                value={solutionForm.content}
                onChange={(e) => setSolutionForm(prev => ({ ...prev, content: e.target.value }))}
                placeholder="Explain the approach and key insights..."
                rows={4}
                data-testid="textarea-solution-content"
              />
            </div>

            <div>
              <Label htmlFor="solution-sql">SQL Code</Label>
              <Textarea
                id="solution-sql"
                value={solutionForm.sql_code}
                onChange={(e) => setSolutionForm(prev => ({ ...prev, sql_code: e.target.value }))}
                placeholder="SELECT ..."
                rows={8}
                className="font-mono text-sm"
                data-testid="textarea-solution-sql"
              />
            </div>

            <div className="flex justify-end">
              <Button 
                onClick={handleSubmit}
                disabled={!solutionForm.title?.trim() || !solutionForm.sql_code?.trim() || saveSolutionMutation.isPending}
                data-testid="button-submit-solution"
              >
                <Save className="w-4 h-4 mr-2" />
                {saveSolutionMutation.isPending 
                  ? 'Saving...' 
                  : existingSolution 
                    ? 'Update Solution' 
                    : 'Create Solution'
                }
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {!selectedProblemId && (
        <Alert>
          <AlertDescription>
            Please select a problem above to create or edit its solution.
          </AlertDescription>
        </Alert>
      )}
    </div>
  );
}

