import { useState, useEffect, createContext, useContext } from 'react';
import { authApi } from '@/lib/auth';

interface User {
  id: string;
  username: string;
  email: string;
  firstName?: string;
  lastName?: string;
  profileImageUrl?: string;
  xp: number;
  level: string;
  problemsSolved: number;
  premium?: boolean;
  isAdmin?: boolean;
}

interface AuthContextType {
  user: User | null;
  token: string | null;
  login: (token: string, user: User) => void;
  logout: () => void;
  isAuthenticated: boolean;
  isLoading: boolean;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [token, setToken] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const initializeAuth = async () => {
      // Development bypass - only active if VITE_DEV_AUTH_MODE is enabled
      if (import.meta.env.DEV && import.meta.env.VITE_DEV_AUTH_MODE === 'true') {
        // Generate or retrieve unique dev user ID for this browser
        let devUserId = localStorage.getItem('dev_user_id');
        if (!devUserId) {
          devUserId = `dev-${crypto.randomUUID()}`;
          localStorage.setItem('dev_user_id', devUserId);
        }

        const devToken = `dev-token::${devUserId}`;

        // Store the token BEFORE making the API call so apiRequest can use it
        localStorage.setItem('auth_token', devToken);

        try {
          // Try to fetch user data from API with dev token
          const userData = await authApi.getCurrentUser();
          setUser(userData);
          setToken(devToken);
          localStorage.setItem('auth_user', JSON.stringify(userData));
        } catch (error) {
          // If API fails, user will need to authenticate properly
          console.log('Dev mode: API unavailable, please authenticate', error);
        }
        setIsLoading(false);
        return;
      }

      try {
        // Always attempt to fetch current user data from backend
        // This supports both token-based and cookie-based authentication
        const userData = await authApi.getCurrentUser();
        setUser(userData);

        // Get token from localStorage if available (for token-based auth)
        const storedToken = localStorage.getItem('auth_token');
        if (storedToken) {
          setToken(storedToken);
        }

        // Update localStorage with fresh user data
        localStorage.setItem('auth_user', JSON.stringify(userData));
      } catch (error) {
        // Authentication failed - clear all auth state
        setToken(null);
        setUser(null);
        localStorage.removeItem('auth_token');
        localStorage.removeItem('auth_user');
      } finally {
        setIsLoading(false);
      }
    };

    initializeAuth();
  }, []);

  const login = (newToken: string, newUser: User) => {
    setToken(newToken);
    setUser(newUser);
    localStorage.setItem('auth_token', newToken);
    localStorage.setItem('auth_user', JSON.stringify(newUser));
  };

  const logout = async () => {
    try {
      await authApi.logout();
    } catch (error) {
      console.error('Logout error:', error);
    }
    setToken(null);
    setUser(null);
    localStorage.removeItem('auth_token');
    localStorage.removeItem('auth_user');
  };

  const value = {
    user,
    token,
    login,
    logout,
    isAuthenticated: !!user,
    isLoading,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

